INSERT INTO `bmw_cars` (`car_id`, `name`, `moto`, `description`, `image`, `sort`) VALUES
(1, 'BMW 1 Series', 'LIFE IS BETTER SHARED FOR REAL', 'The car that is both fun and elegant. The BMW \n1 Series’ light body, agile engineering and \npowerful engine makes it the most dynamic \ncar in its class.\n', 'http://bmw.ps/_common/shared/newvehicles/1series/5door/2015/showroom/gallery/preview/bmw_1_series_preview_612x383_04.jpg', 1),
(2, 'BMW 3 Series', 'DRIVING PLEASURE UNMATCHED.', 'The car that stays true to its racing history. The \nfounder of the sports saloon segment, the \nBMW 3 Series is fast and comfortable.', 'http://bmw.ps/_common/shared/newvehicles/3series/gran_turismo/2013/showroom/gallery/_img/Preview_612x383/BMW_3series_wallpaper_1_612x383.jpg', 3),
(3, 'BMW 2 Series', 'LESS RULES. MORE PERFORMANCE.', 'Feisty characteristics: the striking crease line originates behind the front axle, runs upwards in the rear wing and emphasises the sporty-dynamic wedge shape. Combined with the muscular wheel housings it proves that the BMW 2 Series Coupé is always ready to take the lead.\r\n', 'http://bmw.ps/_common/shared/newvehicles/2series/coupe/2013/gallery/preview/16.jpg', 2),
(4, 'BMW 4 Series', 'DESIGNED FOR DRIVING PLEASURE', 'Dynamism that moves – even when standing still. The design of the BMW 4 Series Coupé radiates a fascinating athletic presence and signifies sheer driving pleasure. A promise that is not only fulfilled during the journey. But is far exceeded.\r\n', 'http://bmw.ps/_common/shared/newvehicles/4series/gran_coupe/2014/keep_informed/gallery/preview/05.jpg', 4),
(5, 'BMW 5 Series', 'OUTSTANDING. STYLISH.', 'The perfect business saloon. The BMW 5 Series\neffortlessly blends business and leisure into an \ninnovative, stylish and efficient car.', 'http://bmw.ps/_common/shared/newvehicles/5series/sedan/2013/showroom/gallery/img/Preview_612x383/preview-03-5-series-sedan-612x383.jpg', 5),
(6, 'BMW 7 Series', 'THE ALL-NEW BMW 7 SERIES.', 'The ultimate luxury car. As BMW’s flagship car, \r\nthe BMW 7 Series is a pioneer in the luxury car \r\nsegment and automotive technology and \r\ndesign.\r\n', 'http://104.131.19.145/bmw_app/car_images/bmw_7.png', 6),
(7, 'BMW X', 'EMBRACE THE UNKNOWN.', 'The BMW X breaks new ground instead of following established routines. From the very first glance, it shows that it is an X model through and through: the short overhangs and long wheelbase typical of an SAV (Sports Activity Vehicle) define its exterior. Inside, too, the X philosophy is consistently maintained. Surrounded by high-quality materials and an intuitive operating concept, the BMW X1 treats its passengers to an elevated seating position. And the interior is not only extremely welcoming but also exceptionally variable: a second adjustable row of seats with an electrically folding backrest adapts to your plans within seconds. At the same time, the efficient and dynamic petrol and diesel engines with BMW EfficientDynamics technologies whet your appetite for the next stage.\n', 'http://104.131.19.145/bmw_app/car_images/BMW_X1.png', 7),
(12, 'BMW M', 'ONE WITH THE MACHINE.', 'High performance, outstanding technology and the true sensation of motor sports can be expressed in one single letter: M. M Power World offers exclusive information on M high-performance cars – and is the place for everyone united by a common passion: the thrill of the strongest letter in the world.\n', 'http://bmw.ps/_common/shared/newvehicles/mseries/m3_sedan/2014/showroom/gallery/preview/f80-preview-12.jpg', 11);
